import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import V_RegistrationPopup from './V_RegistrationPopup'; // Import the popup component
// import './AuthForm.css'; 

function V_LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isPopupOpen, setIsPopupOpen] = useState(false); // State to control popup
    const navigate = useNavigate();

    // Handle form submission for login
    const handleLogin = (e) => {
        e.preventDefault();
        console.log('Login successful with email:', email);
        navigate('/V_dashboard'); // Redirect to dashboard after login
    };

    // Handle popup open/close for registration
    const openPopup = () => setIsPopupOpen(true);
    const closePopup = () => setIsPopupOpen(false);

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" className="auth-button">Login</button>
                </form>
                <p>
                    <button onClick={openPopup} className="register-link">
                        Register the Vehicle
                    </button>
                </p>

                {isPopupOpen && <V_RegistrationPopup onClose={closePopup} />}
            </div>
        </div>
    );
}

export default V_LoginPage;
