import React, { useState } from 'react';

const V_RegistrationPopup = ({ onClose }) => {
    const [formData, setFormData] = useState({
        id: '',
        email: '',
        name: '',
        password: '',
        vehicleNumber: '',
        fuelQuota: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle the registration logic here, e.g., save data to the server
        console.log('Registration Data:', formData);
        onClose(); // Close the popup after form submission
    };

    return (
        <div className="popup-overlay">
            <div className="popup-content">
                <h2>Register Vehicle</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="id"
                        placeholder="ID"
                        value={formData.id}
                        onChange={handleChange}
                    />
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="name"
                        placeholder="Name"
                        value={formData.name}
                        onChange={handleChange}
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="vehicleNumber"
                        placeholder="Vehicle Number Plate"
                        value={formData.vehicleNumber}
                        onChange={handleChange}
                    />
                    <input
                        type="number"
                        name="fuelQuota"
                        placeholder="Fuel Quota"
                        value={formData.fuelQuota}
                        onChange={handleChange}
                    />
                    <button type="submit" className="auth-button">Register</button>
                </form>
                <button className="close-button" onClick={onClose}>Close</button>
            </div>
        </div>
    );
};

export default V_RegistrationPopup;
