import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainPage.css'; 
import F_HomePage from './F_HomePage.js';
import V_HomePage from './V_HomePage.js';

const MainPage = () => {
  const navigate = useNavigate();

  const goToUserPage = () => {
    navigate('/vehicle-owner');
  };

  const goToAdminPage = () => {
    navigate('/fuel-station');
  };

  return (
    <div className='homeContainer'>
      <div style={styles.container}>
        <h1>Fuel Quota Management System</h1>
        <p style={styles.description}>
          Welcome to the Fuel Quota Management System, designed to help manage fuel distribution during critical times. 
          This system enables vehicle owners to register their vehicles and track their allocated fuel quota. 
          Fuel station operators can use the system to verify vehicle details and available fuel quotas through QR code scanning. 
          It's an efficient and secure solution to ensure fair fuel distribution while maintaining transparency.
        </p>
        <div style={styles.buttonContainer}>
          <button style={styles.button} onClick={goToUserPage}>
            User Login
          </button>
          <button style={styles.button} onClick={goToAdminPage}>
            Admin Login
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '50px',
  },
  description: {
    marginTop: '20px',
    fontSize: '16px',
    color: '#555',
    lineHeight: '1.5',
    maxWidth: '600px',
    margin: '0 auto',
  },
  buttonContainer: {
    marginTop: '30px',
  },
  button: {
    margin: '10px',
    padding: '10px 20px',
    fontSize: '18px',
    cursor: 'pointer',
  },
};

export default MainPage;
